import matplotlib.pyplot as plt
import numpy as np
import yfinance as yf
import matplotlib.dates as mdates



def graph_data(stock):
    fig = plt.figure()
    ax1 = fig.add_subplot(111)
    #1 row, 1 column, sublplot1
    # stock_price_url = 'http://chartapi.finance.yahoo.com/instrument/1.0/' + stock + '/chartdata;type=quote;range=10y/csv'
    # source_code = urllib.request.urlopen(stock_price_url).read().decode()
    
   
    stock_data = yf.Ticker(stock)
    df = stock_data.history(period='10y')
    dates = df.index  
    if df.empty or 'Close' not in df.columns or 'High' not in df.columns or 'Low' not in df.columns:
        print(f"No valid data available for {stock}")
    print(df)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    

    
    # plt.plot(df.index, df['High'], label='High Prices', color='blue', alpha=0.7)
    # plt.plot(df.index, df['Low'], label='Low Price', color='red', alpha=0.7)
    ax1.plot(df.index, df['High'], label='High Prices', color='blue', alpha=0.7)
    ax1.plot(df.index, df['Low'], color='red', alpha=0.7)
    plt.xticks(rotation=45)
    
    plt.grid(True, color='g', linestyle='-')
    plt.fill_between(df.index, df['High'], df['Low'], color='gray', alpha=0.3, label='Price Range')
    plt.xlabel('Date')
    plt.ylabel('Price')
    plt.title('Interesting Graph\nCheck it out')
    plt.subplots_adjust(left=0.09, bottom=0.20, right=0.94, top=0.90, wspace=0.2, hspace=0)
    plt.legend()
    plt.show()
graph_data('TSLA')
